import React from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const summary = [
  { label: "Total Projects", value: 8, icon: "folder_open", color: "from-blue-500 to-blue-700" },
  { label: "Earnings", value: "$4,200", icon: "attach_money", color: "from-green-400 to-green-600" },
  { label: "Tasks Due", value: 5, icon: "assignment", color: "from-yellow-400 to-yellow-600" },
];

const activity = [
  { id: 1, text: "Completed task: Logo Design", icon: "check_circle", color: "text-green-500" },
  { id: 2, text: "Added new project: App UI", icon: "add_circle", color: "text-blue-500" },
  { id: 3, text: "Updated profile info", icon: "person", color: "text-yellow-500" },
];

const chartData = {
  labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
  datasets: [
    {
      label: "Earnings ($)",
      data: [800, 1200, 900, 1500, 1100, 1400],
      backgroundColor: "#38bdf8",
      borderRadius: 8,
      barPercentage: 0.7,
    },
  ],
};

export default function Overview() {
  return (
    <div className="space-y-8">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {summary.map((s) => (
          <div
            key={s.label}
            className={`bg-gradient-to-br ${s.color} rounded-2xl shadow-xl p-8 flex flex-col items-center justify-center transition-transform hover:scale-105`}
          >
            <span className="material-icons text-white text-4xl mb-2 drop-shadow-lg">{s.icon}</span>
            <div className="text-3xl font-extrabold text-white drop-shadow-lg">{s.value}</div>
            <div className="text-white/80 mt-2 text-lg font-medium tracking-wide">{s.label}</div>
          </div>
        ))}
      </div>
      <div className="grid md:grid-cols-2 gap-6">
        <div className="bg-white rounded-2xl shadow-xl p-8 flex flex-col">
          <div className="font-bold text-blue-900 text-xl mb-4 flex items-center gap-2">
            <span className="material-icons text-blue-400">bar_chart</span>
            Monthly Earnings
          </div>
          <Bar
            data={chartData}
            options={{
              responsive: true,
              plugins: { legend: { display: false } },
              animation: { duration: 1200 },
            }}
          />
        </div>
        <div className="bg-white rounded-2xl shadow-xl p-8">
          <div className="font-bold text-blue-900 text-xl mb-4 flex items-center gap-2">
            <span className="material-icons text-yellow-400">history</span>
            Recent Activity
          </div>
          <ul className="space-y-4">
            {activity.map((a) => (
              <li key={a.id} className="flex items-center gap-3 text-lg font-medium">
                <span className={`material-icons text-2xl ${a.color}`}>{a.icon}</span>
                <span className="text-blue-900">{a.text}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
