import React from "react";
import { Bar } from "react-chartjs-2";

const statsData = {
  labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
  datasets: [
    {
      label: "Projects",
      data: [5, 8, 6, 10, 7, 9],
      backgroundColor: "#7C3AED",
    },
    {
      label: "Applications",
      data: [12, 15, 14, 18, 16, 20],
      backgroundColor: "#FBBF24",
    },
  ],
};

export default function Statistics() {
  return (
    <div className="max-w-2xl mx-auto mt-12 bg-white rounded-2xl shadow-xl p-8">
      <h2 className="text-2xl font-bold mb-6 text-[#7C3AED]">Statistics</h2>
      <div className="w-full flex justify-center">
        <div style={{ maxWidth: 600, width: "100%", height: 260 }}>
          <Bar data={statsData} options={{ responsive: true, plugins: { legend: { position: "top" } }, maintainAspectRatio: true }} />
        </div>
      </div>
    </div>
  );
}
