import React from "react";
import { NavLink } from "react-router-dom";

const navItems = [
  { name: "Overview", path: "/overview", icon: "dashboard" },
  { name: "Projects", path: "/projects", icon: "folder" },
  { name: "Profile", path: "/profile", icon: "person" },
  { name: "Settings", path: "/settings", icon: "settings" },
];

export default function Sidebar() {
  return (
    <aside className="w-64 h-full hidden md:flex flex-col bg-gradient-to-b from-blue-700 via-blue-800 to-gray-900 shadow-xl">
      <div className="p-6 text-white font-extrabold text-2xl tracking-wide flex items-center gap-2 border-b border-blue-900">
        <span className="material-icons text-yellow-400 text-3xl">workspace_premium</span>
        Freelance Admin
      </div>
      <nav className="mt-8 flex-1">
        {navItems.map((item) => (
          <NavLink
            key={item.path}
            to={item.path}
            className={({ isActive }) =>
              `flex items-center gap-3 px-6 py-3 text-lg font-medium rounded-lg transition-all duration-200 mb-2 ${
                isActive
                  ? "bg-blue-600 text-white shadow-lg"
                  : "text-blue-200 hover:bg-blue-900 hover:text-white"
              }`
            }
          >
            <span className="material-icons text-2xl">{item.icon}</span>
            {item.name}
          </NavLink>
        ))}
      </nav>
      <div className="mt-auto p-6 text-xs text-blue-300 opacity-70">
        <span className="material-icons align-middle mr-1">copyright</span>
        2026 Elevvo Internship
      </div>
    </aside>
  );
}
