import React, { useState } from "react";

const mockNotifications = [
  { id: 1, message: "New project assigned: Website Redesign", time: "2m ago" },
  { id: 2, message: "Invoice #1234 paid", time: "1h ago" },
  { id: 3, message: "Profile updated successfully", time: "3h ago" },
];

export default function NotificationDropdown() {
  const [open, setOpen] = useState(false);

  return (
    <div className="relative">
      <button
        className="relative focus:outline-none"
        onClick={() => setOpen((o) => !o)}
        aria-label="Show notifications"
      >
        <span className="material-icons text-gray-600 text-2xl">notifications</span>
        <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full px-1.5">
          {mockNotifications.length}
        </span>
      </button>
      {open && (
        <div className="absolute right-0 mt-2 w-72 bg-white shadow-lg rounded-md z-20">
          <div className="p-3 border-b font-semibold">Notifications</div>
          <ul>
            {mockNotifications.map((n) => (
              <li key={n.id} className="px-4 py-2 hover:bg-gray-100 text-sm flex justify-between">
                <span>{n.message}</span>
                <span className="text-gray-400 ml-2 whitespace-nowrap">{n.time}</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
