import React from "react";
import { NavLink } from "react-router-dom";

const navItems = [
  { name: "Overview", path: "/overview" },
  { name: "Projects", path: "/projects" },
  { name: "Profile", path: "/profile" },
];

export default function Sidebar() {
  return (
    <aside className="w-64 bg-white shadow-md h-full hidden md:block">
      <div className="p-6 font-bold text-xl border-b">Freelance Admin</div>
      <nav className="mt-6">
        {navItems.map((item) => (
          <NavLink
            key={item.path}
            to={item.path}
            className={({ isActive }) =>
              `block px-6 py-3 text-gray-700 hover:bg-gray-100 transition ${isActive ? "bg-gray-200 font-semibold" : ""}`
            }
          >
            {item.name}
          </NavLink>
        ))}
      </nav>
    </aside>
  );
}
