import React from "react";
import { NavLink } from "react-router-dom";

const navItems = [
  { name: "Dashboard", path: "/overview", icon: "dashboard" },
  { name: "Search Job", path: "/projects", icon: "search" },
  { name: "Applications", path: "/profile", icon: "assignment" },
  { name: "Message", path: "/settings", icon: "mail" },
  { name: "Statistics", path: "/stats", icon: "bar_chart" },
];

export default function Sidebar() {
  return (
    <aside className="w-72 h-full hidden md:flex flex-col bg-[#5B2EFF] rounded-r-3xl shadow-2xl">
      <div className="flex items-center gap-3 p-8 pb-4">
        <div className="bg-white rounded-full w-12 h-12 flex items-center justify-center shadow-lg">
          <span className="material-icons text-[#5B2EFF] text-3xl">work</span>
        </div>
        <span className="text-white font-extrabold text-2xl tracking-wide">Freelance Admin Portal</span>
      </div>
      <nav className="mt-6 flex-1">
        {navItems.map((item) => (
          <NavLink
            key={item.path}
            to={item.path}
            className={({ isActive }) =>
              `flex items-center gap-4 px-8 py-4 text-lg font-semibold rounded-xl transition-all duration-200 mb-2 ${
                isActive
                  ? "bg-white text-[#5B2EFF] shadow-lg"
                  : "text-white hover:bg-[#6C3EFF] hover:text-white"
              }`
            }
          >
            <span className="material-icons text-2xl">{item.icon}</span>
            {item.name}
          </NavLink>
        ))}
      </nav>
      <div className="mt-auto p-8 text-xs text-white/70">
        <span className="material-icons align-middle mr-1">copyright</span>
        2026 Elevvo Internship
      </div>
    </aside>
  );
}
