import React from "react";
import NotificationDropdown from "./NotificationDropdown";

export default function Header() {
  return (
    <header className="flex items-center justify-between bg-gradient-to-r from-blue-700 via-blue-800 to-gray-900 px-6 py-4 shadow-lg sticky top-0 z-20">
      <div className="font-bold text-xl text-white md:hidden flex items-center gap-2">
        <span className="material-icons text-yellow-400 text-2xl">workspace_premium</span>
        Freelance Admin
      </div>
      <div className="flex items-center gap-4 ml-auto">
        <NotificationDropdown />
        <img
          src="https://i.pravatar.cc/40"
          alt="avatar"
          className="w-10 h-10 rounded-full border-4 border-yellow-400 shadow-md transition-transform hover:scale-105"
        />
      </div>
    </header>
  );
}
