import React, { useState } from "react";

export default function Profile() {
  const [form, setForm] = useState({
    name: "Jane Doe",
    email: "jane@example.com",
    password: "password123",
  });

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleSubmit(e) {
    e.preventDefault();
    alert("Profile updated!");
  }

  return (
    <div className="bg-white rounded shadow p-6 max-w-md mx-auto">
      <div className="font-semibold text-lg mb-4">Profile Settings</div>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block mb-1 font-medium">Name</label>
          <input
            className="w-full border px-3 py-2 rounded"
            name="name"
            value={form.name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label className="block mb-1 font-medium">Email</label>
          <input
            className="w-full border px-3 py-2 rounded"
            name="email"
            type="email"
            value={form.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label className="block mb-1 font-medium">Password</label>
          <input
            className="w-full border px-3 py-2 rounded"
            name="password"
            type="password"
            value={form.password}
            onChange={handleChange}
            required
          />
        </div>
        <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600" type="submit">
          Save Changes
        </button>
      </form>
    </div>
  );
}
