import React from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Sidebar from "./layout/Sidebar";
import Header from "./layout/Header";
import Overview from "./pages/Overview";
import Projects from "./pages/Projects";
import Profile from "./pages/Profile";

function App() {
  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Header />
        <main className="flex-1 p-4 overflow-auto">
          <Routes>
            <Route path="/" element={<Navigate to="/overview" />} />
            <Route path="/overview" element={<Overview />} />
            <Route path="/projects" element={<Projects />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/settings" element={<div className="bg-white rounded-2xl shadow-xl p-8 max-w-lg mx-auto mt-8 text-blue-900 text-2xl font-bold flex items-center gap-2"><span className="material-icons text-blue-400">settings</span>Settings (Coming Soon)</div>} />
          </Routes>
        </main>
      </div>
    </div>
  );
}

export default App;
