import React from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const summary = [
  { label: "Total Projects", value: 8 },
  { label: "Earnings", value: "$4,200" },
  { label: "Tasks Due", value: 5 },
];

const activity = [
  { id: 1, text: "Completed task: Logo Design" },
  { id: 2, text: "Added new project: App UI" },
  { id: 3, text: "Updated profile info" },
];

const chartData = {
  labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
  datasets: [
    {
      label: "Earnings ($)",
      data: [800, 1200, 900, 1500, 1100, 1400],
      backgroundColor: "#3b82f6",
    },
  ],
};

export default function Overview() {
  return (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {summary.map((s) => (
          <div key={s.label} className="bg-white rounded shadow p-6 text-center">
            <div className="text-2xl font-bold">{s.value}</div>
            <div className="text-gray-500 mt-2">{s.label}</div>
          </div>
        ))}
      </div>
      <div className="grid md:grid-cols-2 gap-4">
        <div className="bg-white rounded shadow p-6">
          <div className="font-semibold mb-2">Monthly Earnings</div>
          <Bar data={chartData} options={{ responsive: true, plugins: { legend: { display: false } } }} />
        </div>
        <div className="bg-white rounded shadow p-6">
          <div className="font-semibold mb-2">Recent Activity</div>
          <ul className="space-y-2">
            {activity.map((a) => (
              <li key={a.id} className="text-gray-700 flex items-center gap-2">
                <span className="material-icons text-blue-400">check_circle</span>
                {a.text}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
